import React, { useState, useEffect } from 'react';
import axios from 'axios';

const InstructorDashboard = () => {
  const [courses, setCourses] = useState([]);
  const [newCourse, setNewCourse] = useState({ name: '', description: '' });
  const [assignments, setAssignments] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState(null);
  const instructorId = 1; // Replace with dynamic ID from token or context

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const response = await axios.get(
          `http://localhost/e-learning/backend/instructor_courses.php?instructor_id=${instructorId}`
        );
        setCourses(response.data);
      } catch (error) {
        console.error('Error fetching courses:', error);
      }
    };

    fetchCourses();
  }, []);

  const handleAddCourse = async () => {
    try {
      const response = await axios.post('http://localhost/e-learning/backend/add_course.php', {
        instructor_id: instructorId,
        course_name: newCourse.name,
        description: newCourse.description,
      });
      alert(response.data.message);
      setCourses([...courses, response.data.course]);
      setNewCourse({ name: '', description: '' });
    } catch (error) {
      console.error('Error adding course:', error);
    }
  };

  const fetchAssignments = async (courseId) => {
    try {
      const response = await axios.get(
        `http://localhost/e-learning/backend/course_assignments.php?course_id=${courseId}`
      );
      setAssignments(response.data);
      setSelectedCourse(courseId);
    } catch (error) {
      console.error('Error fetching assignments:', error);
    }
  };

  return (
    <div>
      <h1>Instructor Dashboard</h1>
      <h2>My Courses</h2>
      <ul>
        {courses.map((course) => (
          <li key={course.course_id}>
            {course.course_name}
            <button onClick={() => fetchAssignments(course.course_id)}>View Assignments</button>
          </li>
        ))}
      </ul>

      <h3>Add New Course</h3>
      <input
        type="text"
        placeholder="Course Name"
        value={newCourse.name}
        onChange={(e) => setNewCourse({ ...newCourse, name: e.target.value })}
      />
      <input
        type="text"
        placeholder="Description"
        value={newCourse.description}
        onChange={(e) => setNewCourse({ ...newCourse, description: e.target.value })}
      />
      <button onClick={handleAddCourse}>Add Course</button>

      {selectedCourse && (
        <div>
          <h3>Assignments for Course ID: {selectedCourse}</h3>
          <ul>
            {assignments.map((assignment) => (
              <li key={assignment.assignment_id}>
                {assignment.file_name} - Submitted by Student {assignment.student_id}
                <button>Grade</button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default InstructorDashboard;
