import React, { useState, useEffect } from 'react';
import axios from 'axios';

const StudentDashboard = () => {
  const [courses, setCourses] = useState([]);
  const [comments, setComments] = useState('');
  const [selectedCourse, setSelectedCourse] = useState(null);

  const studentId = 1; // Replace with dynamic ID

  useEffect(() => {
    const fetchCourses = async () => {
      const response = await axios.get(`http://localhost/e-learning/backend/view_courses.php?student_id=${studentId}`);
      setCourses(response.data);
    };

    fetchCourses();
  }, []);

  const handleEnroll = async (courseId) => {
    await axios.post('http://localhost/e-learning/backend/enroll.php', {
      student_id: studentId,
      course_id: courseId,
    });
    alert('Enrolled successfully!');
  };

  const handleAssignmentUpload = async (courseId, file) => {
    const formData = new FormData();
    formData.append('course_id', courseId);
    formData.append('student_id', studentId);
    formData.append('assignment', file);

    await axios.post('http://localhost/e-learning/backend/upload_assignment.php', formData);
    alert('Assignment submitted!');
  };

  return (
    <div>
      <h1>Student Dashboard</h1>
      <h2>My Courses</h2>
      <ul>
        {courses.map((course) => (
          <li key={course.course_id}>
            {course.course_name} - {course.description}
            <button onClick={() => setSelectedCourse(course.course_id)}>View</button>
          </li>
        ))}
      </ul>

      {selectedCourse && (
        <>
          <h3>Submit Assignment</h3>
          <input
            type="file"
            onChange={(e) => handleAssignmentUpload(selectedCourse, e.target.files[0])}
          />
          <h3>Post Comment</h3>
          <textarea onChange={(e) => setComments(e.target.value)} />
          <button
            onClick={() =>
              axios.post('http://localhost/e-learning/backend/post_comment.php', {
                course_id: selectedCourse,
                student_id: studentId,
                content: comments,
                is_private: false,
              })
            }
          >
            Post Comment
          </button>
        </>
      )}
    </div>
  );
};

export default StudentDashboard;
