import React from 'react';
import { Routes, Route, Navigate, useLocation } from 'react-router-dom';
import NavBar from './components/NavBar';
import Footer from './components/Footer';
import Login from './components/Login';
import Signup from './components/Signup';
import StudentDashboard from './components/StudentDashboard';
import InstructorDashboard from './components/InstructorDashboard';
import AdminDashboard from './components/AdminDashboard';

const App = () => {
  const location = useLocation();
  const token = localStorage.getItem('token');
  
  // Decode JWT and extract user role
  const userRole = token ? (() => {
    try {
      return JSON.parse(atob(token.split('.')[1])).role;
    } catch (error) {
      console.error('Invalid token:', error);
      return null;
    }
  })() : null;

  const isAuthenticated = !!token; // Check if user is logged in
  const isAuthRoute = ['/login', '/register'].includes(location.pathname); // Define auth routes
  const showNavFooter = isAuthenticated && !isAuthRoute; // Show NavBar/Footer based on auth state

  return (
    <div>
      {/* Conditionally show the NavBar */}
      {showNavFooter && <NavBar />}
      <div className="content">
        <Routes>
          {/* Redirect root to login */}
          <Route path="/" element={<Navigate to="/login" replace />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Signup />} />

          {/* Protected routes based on user role */}
          {isAuthenticated ? (
            <>
              {userRole === 'student' && (
                <Route path="/student" element={<StudentDashboard />} />
              )}
              {userRole === 'instructor' && (
                <Route path="/instructor" element={<InstructorDashboard />} />
              )}
              {userRole === 'admin' && (
                <Route path="/admin" element={<AdminDashboard />} />
              )}
              {/* Redirect unknown routes for authenticated users */}
              <Route path="*" element={<Navigate to={`/${userRole}`} replace />} />
            </>
          ) : (
            // Redirect unknown routes for unauthenticated users
            <Route path="*" element={<Navigate to="/login" replace />} />
          )}
        </Routes>
      </div>
      {/* Conditionally show the Footer */}
      {showNavFooter && <Footer />}
    </div>
  );
};

export default App;
