import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import NavBar from './components/NavBar';
import Footer from './components/Footer';
import Login from './components/login';
import Signup from './components/signup';
import StudentPage from './components/student';
import InstructorPage from './components/instructor';
import AdminPage from './components/admin';

const App = () => {
  const location = useLocation();

  const isAuthenticated = localStorage.getItem('token'); // Example logic; replace as needed

  const showNavFooter = isAuthenticated && !['/login', '/register'].includes(location.pathname);

  return (
    <div>
      {showNavFooter && <NavBar />}
      <div className="content">
        <Routes>
          <Route path="/" element={<Login />} />
          <Route path="/register" element={<Signup />} />
          {isAuthenticated && (
            <>
              <Route path="/student" element={<StudentPage />} />
              <Route path="/instructor" element={<InstructorPage />} />
              <Route path="/admin" element={<AdminPage />} />
            </>
          )}
        </Routes>
      </div>
      {showNavFooter && <Footer />}
    </div>
  );
};

export default App;
