import React from 'react';
import { Routes, Route, Navigate, useLocation } from 'react-router-dom';
import NavBar from './components/NavBar';
import Footer from './components/Footer';
import Login from './components/Login';
import Signup from './components/Signup';
import StudentDashboard from './components/StudentDashboard';
import InstructorDashboard from './components/InstructorDashboard';
import AdminDashboard from './components/AdminDashboard';

const App = () => {
  const location = useLocation();
  const token = localStorage.getItem('token');

  // Safely decode the token and extract user role
  const userRole = token ? (() => {
    try {
      const decoded = JSON.parse(atob(token.split('.')[1]));
      return decoded.role || null; // Ensure role exists in payload
    } catch (error) {
      console.error('Token decoding failed:', error);
      return null;
    }
  })() : null;

  const isAuthenticated = !!token; // Check if token exists
  const isAuthRoute = ['/login', '/register'].includes(location.pathname); // Authentication routes
  const showNavFooter = isAuthenticated && !isAuthRoute; // Conditional NavBar/Footer

  // Handle unknown roles
  if (isAuthenticated && !userRole) {
    console.error('Invalid or missing user role.');
    localStorage.removeItem('token'); // Clear invalid token
    return <Navigate to="/login" replace />;
  }

  return (
    <div>
      {showNavFooter && <NavBar />}
      <div className="content">
        <Routes>
          <Route path="/" element={<Navigate to="/login" replace />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Signup />} />

          {isAuthenticated ? (
            <>
              {userRole === 'student' && (
                <Route path="/student" element={<StudentDashboard />} />
              )}
              {userRole === 'instructor' && (
                <Route path="/instructor" element={<InstructorDashboard />} />
              )}
              {userRole === 'admin' && (
                <Route path="/admin" element={<AdminDashboard />} />
              )}
              <Route path="*" element={<Navigate to={`/${userRole}`} replace />} />
            </>
          ) : (
            <Route path="*" element={<Navigate to="/login" replace />} />
          )}
        </Routes>
      </div>
      {showNavFooter && <Footer />}
    </div>
  );
};

export default App;
