import React, { useState, useEffect } from 'react';
import axios from 'axios';

const StudentDashboard = () => {
  const [courses, setCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [assignment, setAssignment] = useState(null);
  const [comment, setComment] = useState('');
  const studentId = 1; // Replace with actual ID

  useEffect(() => {
    axios
      .get(`http://localhost/e-learning/backend/view_courses.php?student_id=${studentId}`)
      .then((response) => setCourses(response.data))
      .catch((error) => console.error('Error fetching courses:', error));
  }, []);

  const handleEnroll = (courseId) => {
    axios
      .post('http://localhost/e-learning/backend/enroll.php', {
        student_id: studentId,
        course_id: courseId,
      })
      .then((response) => alert(response.data.message))
      .catch((error) => console.error('Error enrolling:', error));
  };

  const handleAssignmentUpload = () => {
    const formData = new FormData();
    formData.append('course_id', selectedCourse);
    formData.append('student_id', studentId);
    formData.append('assignment', assignment);

    axios
      .post('http://localhost/e-learning/backend/upload_assignment.php', formData)
      .then((response) => alert(response.data.message))
      .catch((error) => console.error('Error uploading assignment:', error));
  };

  const handlePostComment = (isPrivate) => {
    axios
      .post('http://localhost/e-learning/backend/post_comment.php', {
        course_id: selectedCourse,
        student_id: studentId,
        content: comment,
        is_private: isPrivate,
      })
      .then((response) => alert(response.data.message))
      .catch((error) => console.error('Error posting comment:', error));
  };

  return (
    <div>
      <h1>Student Dashboard</h1>
      <h2>Available Courses</h2>
      <ul>
        {courses.map((course) => (
          <li key={course.course_id}>
            {course.course_name}
            <button onClick={() => handleEnroll(course.course_id)}>Enroll</button>
            <button onClick={() => setSelectedCourse(course.course_id)}>View</button>
          </li>
        ))}
      </ul>

      {selectedCourse && (
        <div>
          <h3>Submit Assignment</h3>
          <input type="file" onChange={(e) => setAssignment(e.target.files[0])} />
          <button onClick={handleAssignmentUpload}>Upload</button>

          <h3>Post Comment</h3>
          <textarea value={comment} onChange={(e) => setComment(e.target.value)} />
          <button onClick={() => handlePostComment(false)}>Public</button>
          <button onClick={() => handlePostComment(true)}>Private</button>
        </div>
      )}
    </div>
  );
};

export default StudentDashboard;
