import React from 'react';
import { Routes, Route, useLocation, Navigate } from 'react-router-dom';
import jwtDecode from 'jwt-decode'; // Install using npm install jwt-decode
import NavBar from './components/NavBar';
import Footer from './components/Footer';
import Login from './components/Login';
import Signup from './components/Signup';
import StudentPage from './components/StudentDashboard';
import InstructorPage from './components/InstructorDashboard';
import AdminPage from './components/AdminDashboard';

const App = () => {
  const location = useLocation();
  const token = localStorage.getItem('token');
  
  let userRole = null;

  if (token) {
    try {
      const decodedToken = jwtDecode(token);
      userRole = decodedToken.role; // Extract role from the decoded token
    } catch (error) {
      console.error('Invalid token:', error);
      localStorage.removeItem('token'); // Remove invalid token
    }
  }

  const isAuthenticated = !!token && !!userRole;
  const authRoutes = ['/login', '/register'];
  const showNavFooter = isAuthenticated && !authRoutes.includes(location.pathname);

  return (
    <div>
      {showNavFooter && <NavBar />}
      <div className="content">
        <Routes>
          {/* Public Routes */}
          <Route path="/" element={<Navigate to="/login" replace />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Signup />} />

          {/* Role-Based Protected Routes */}
          {isAuthenticated ? (
            <>
              {userRole === 'student' && (
                <Route path="/student" element={<StudentPage />} />
              )}
              {userRole === 'instructor' && (
                <Route path="/instructor" element={<InstructorPage />} />
              )}
              {userRole === 'admin' && (
                <Route path="/admin" element={<AdminPage />} />
              )}
              {/* Redirect unknown paths for authenticated users */}
              <Route path="*" element={<Navigate to={`/${userRole}`} replace />} />
            </>
          ) : (
            // Redirect unauthenticated users to login
            <Route path="*" element={<Navigate to="/login" replace />} />
          )}
        </Routes>
      </div>
      {showNavFooter && <Footer />}
    </div>
  );
};

export default App;
