import React from 'react';
import { Routes, Route, useLocation, Navigate } from 'react-router-dom';
import NavBar from './components/NavBar';
import Footer from './components/Footer';
import Login from './components/login';
import Signup from './components/signup';
import StudentPage from './components/student';
import InstructorPage from './components/instructor';
import AdminPage from './components/admin';

const App = () => {
  const location = useLocation();
  const token = localStorage.getItem('token');

  const isAuthenticated = !!token; // Check if token exists
  const authRoutes = ['/login', '/register']; // Routes where NavBar and Footer are hidden
  const showNavFooter = isAuthenticated && !authRoutes.includes(location.pathname);

  return (
    <div>
      {showNavFooter && <NavBar />}
      <div className="content">
        <Routes>
          {/* Default route set to Login */}
          <Route path="/" element={<Navigate to="/login" replace />} />

          {/* Authentication routes */}
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Signup />} />

          {/* Protected routes */}
          {isAuthenticated ? (
            <>
              <Route path="/student" element={<StudentPage />} />
              <Route path="/instructor" element={<InstructorPage />} />
              <Route path="/admin" element={<AdminPage />} />
            </>
          ) : (
            <Route path="*" element={<Navigate to="/login" replace />} />
          )}
        </Routes>
      </div>
      {showNavFooter && <Footer />}
    </div>
  );
};

export default App;
