import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [courses, setCourses] = useState([]);
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost/e-learning/backend/admin_actions.php?action=view_users', {
        headers: { Authorization: localStorage.getItem('token') },
      });
      setUsers(response.data.data || []);
    } catch (error) {
      setMessage('Error fetching users.');
      setMessageType('error');
    }
  };

  const fetchCourses = async () => {
    try {
      const response = await axios.get('http://localhost/e-learning/backend/admin_actions.php?action=view_courses', {
        headers: { Authorization: localStorage.getItem('token') },
      });
      setCourses(response.data.data || []); // Ensure fallback to an empty array
    } catch (error) {
      setMessage('Error fetching courses.');
      setMessageType('error');
    }
  };

  const banUser = async (userId) => {
    try {
      await axios.post(
        'http://localhost/e-learning/backend/admin_actions.php',
        { action: 'ban_user', user_id: userId },
        { headers: { Authorization: localStorage.getItem('token') } }
      );
      setMessage('User banned successfully.');
      setMessageType('success');
      fetchUsers();
    } catch (error) {
      setMessage('Error banning user.');
      setMessageType('error');
    }
  };

  useEffect(() => {
    fetchUsers();
    fetchCourses();
  }, []);

  return (
    <div className="container">
      <h1>Admin Dashboard</h1>
      {message && <p className={`message ${messageType}`}>{message}</p>}

      <h2>Users</h2>
      {users.length > 0 ? (
        <ul>
          {users.map((user) => (
            <li key={user.user_id}>
              {user.username} - {user.role}
              {user.role !== 'admin' && (
                <button onClick={() => banUser(user.user_id)}>Ban</button>
              )}
            </li>
          ))}
        </ul>
      ) : (
        <p>No users available.</p>
      )}

      <h2>Courses</h2>
      {courses.length > 0 ? (
        <ul>
          {courses.map((course) => (
            <li key={course.course_id}>
              {course.course_name} (Instructor ID: {course.instructor_id})
            </li>
          ))}
        </ul>
      ) : (
        <p>No courses available.</p>
      )}
    </div>
  );
};

export default AdminDashboard;
