import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Signup = () => {
  const [signupForm, setSignupForm] = useState({
    username: '',
    password: '',
  });
  const [message, setMessage] = useState(''); // For feedback messages

  const handleSignup = () => {
    const data = new FormData();
    data.append('username', signupForm.username);
    data.append('password', signupForm.password);

    axios
      .post('http://localhost/e-learning/backend/signup.php', data)
      .then((response) => {
        setMessage('Signup successful! You can now log in.');
        setSignupForm({ username: '', password: '' }); // Reset form
      })
      .catch((error) => {
        if (error.response && error.response.status === 409) {
          setMessage('Username already exists. Please try a different one.');
        } else {
          setMessage('Error signing up. Please try again later.');
        }
      });
  };

  return (
    <div>
      <h1>Signup</h1>
      {message && <p>{message}</p>} {/* Display feedback message */}
      <input
        type="text"
        placeholder="Username"
        value={signupForm.username}
        onChange={(e) =>
          setSignupForm({ ...signupForm, username: e.target.value })
        }
      />
      <input
        type="password"
        placeholder="Password"
        value={signupForm.password}
        onChange={(e) =>
          setSignupForm({ ...signupForm, password: e.target.value })
        }
      />
      <button onClick={handleSignup}>Signup</button>
      <p>
        Already have an account? <Link to="/">Login here</Link>
      </p>
    </div>
  );
};

export default Signup;
